export default function Footer(props) {
  return (
    <div>
      {props.gameEnded ? 
      <h1>You scored 3/5 answers correct</h1> :
      undefined
      }
      {props.gameEnded ? (
        <button onClick={props.checkAnswers}>Play Again</button>
      ) : (
        <button onClick={props.checkAnswers}>Check Answers</button>
      )}
    </div>
  );
}
