import React from "react";
import { nanoid } from "nanoid";
import Card from "./Card";
import Footer from "./Footer";
import decoder from "./util";

export default function QuizPage() {
  const [trivia, setTrivia] = React.useState([]);
  const [newGame, setNewGame] = React.useState(false);
  const [gameEnded, setGameEnded] = React.useState(false);



  React.useEffect(() => {
    async function getTrivia() {
      try {
        const response = await fetch(
          "https://opentdb.com/api.php?amount=5&category=31&type=multiple"
        );
        const data = await response.json();
        setTrivia(createTrivia(data.results));
      } catch (err) {
        console.log(err);
      }
    }
    getTrivia();
  }, []);

  function createTrivia(obj) {

    // remove html entities
    const newTriviaQuestions = obj.map((e) => {
      const decodedQuestion = decoder(e.question);
      const decodedCorrectAnswer = decoder(e.correct_answer);
      const decodedIncorrectAnswers = e.incorrect_answers.map((e) => {
        return decoder(e);
      });

      // create array containing all 4 answer choices
      const allChoices = decodedIncorrectAnswers;
      allChoices.push(decodedCorrectAnswer)

      return {
        id: nanoid(),
        question: decodedQuestion,
        correctAnswer: decodedCorrectAnswer,
        allAnswerChoices: allChoices,
        answerChecked: false,
        category: e.category,
        difficulty: e.difficulty,
      };
    });
    return newTriviaQuestions;
  }

  const TriviaCards = trivia.map((e) => {
    return <Card question={e.question} key={e.id} answerChoices={e.allAnswerChoices}/>;
  });


  function checkAnswers() {
    setGameEnded(true);
  }

  console.log("rendered");
  console.log(trivia);

  return (
    <div>
      {TriviaCards}
      <Footer />
    </div>
  );
}
