import React from "react"




export default function QuizPage() {

    const [questions, setQuestions] = React.useState(["hello"])



    React.useEffect(() => {

        function getTriviaQuestions() {
            fetch("https://opentdb.com/api.php?amount=5&category=31&difficulty=easy&type=multiple")
            .then(response => response.json())
            .then(data => setQuestions(data.results))
        }

        getTriviaQuestions()

        console.log("useEffect ran")
    }, [])

    const QuestionCards = () => {
        return (
            questions.map(question => {
                return <Card question={question} />
            })
        )
    }




    return (
        <div>
            <Card question={questions[0].question}/>
            <Card />
            <Card />
            <Card />
            <Card />
            <Footer />
        </div>
    )
}

function Card(props) {
    return(
        <div>
            <p>{props.question}</p>
            <button className="quizpage--button">Adios</button>
            <button className="quizpage--button">Hola</button>
            <button className="quizpage--button">Au Revior</button>
            <button className="quizpage--button">Salir</button>
        </div>
    )
}

function Footer() {
    return (
        <div>
            <h1>You scored 3/5 answers correct</h1>
            <button>Play Again</button>
        </div>
    )
}
