import React from "react";
import { nanoid } from "nanoid";
import { encode, decode } from "js-base64";

export default function QuizPage() {
  const [questions, setQuestions] = React.useState(["hello"]);


  // use setQuestions below and create a callback function that'll turn all the data into an object
  // figure out how to get rid of html characters
  React.useEffect(() => {

    async function getData() {
      const response = await fetch("https://opentdb.com/api.php?amount=5&category=31&difficulty=easy&type=multiple")
      const data = await response.json();
      setQuestions(data.results);
    }

    getData();

    console.log("useEffect ran");
  }, []);

  const QuestionCards = questions.map((question) => {
    return <Card question={question.question} key={nanoid()} />;
  });

  return (
    <div>
      {QuestionCards}
      <Footer />
    </div>
  );
}





// Move to separe files
function Card(props) {
  return (
    <div>
      <p>{props.question}</p>
      <button className="quizpage--button">Adios</button>
      <button className="quizpage--button">Hola</button>
      <button className="quizpage--button">Au Revior</button>
      <button className="quizpage--button">Salir</button>
    </div>
  );
}

function Footer() {
  return (
    <div>
      <h1>You scored 3/5 answers correct</h1>
      <button>Play Again</button>
    </div>
  );
}
